i\\/Lucas_Cranach_l%E2%80%99Ancien\\\">Lucas Cranach l\\u2019Ancien<\\/a>, mais l\\u2019argent de l\\u2019assurance permet de racheter d\\u2019autres tableaux de valeur.<\\/p>\"},\"elements\":[],\"widgetType\":\"text-editor\"}],\"isInner\":false}],\"isInner\":false}]'),(1431,395,'_elementor_version','0.4'),(1601,430,'_menu_item_type','post_type'),(1602,430,'_menu_item_menu_item_parent','425'),(1441,397,'_menu_item_type','post_type'),(1442,397,'_menu_item_menu_item_parent','425'),(1443,397,'_menu_item_object_id','384'),(1444,397,'_menu_item_object','page'),(1445,397,'_menu_item_target',''),(1446,397,'_menu_item_classes','a:1:{i:0;s:0:\"\";}'),(1447,397,'_menu_item_xfn',''),(1448,397,'_menu_item_url',''),(1450,398,'_menu_item_type','post_type'),(1451,398,'_menu_item_menu_item_parent','425'),(1452,398,'_menu_item_object_id','380'),(1453,398,'_menu_item_object','page'),(1454,398,'_menu_item_target',''),(1455,398,'_menu_item_classes','a:1:{i:0;s:0:\"\";}'),(1456,398,'_menu_item_xfn',''),(1457,398,'_menu_item_url',''),(1459,399,'_menu_item_type','post_type'),(1460,399,'_menu_item_menu_item_parent','425'),(1461,399,'_menu_item_object_id','377'),(1462,399,'_menu_item_object','page'),(1463,399,'_menu_item_target',''),(1464,399,'_menu_item_classes','a:1:{i:0;s:0:\"\";}'),(1465,399,'_menu_item_xfn',''),(1466,399,'_menu_item_url',''),(1468,400,'_menu_item_type','post_type'),(1469,400,'_menu_item_menu_item_parent','425'),(1470,400,'_menu_item_object_id','372'),(1471,400,'_menu_item_object','page'),(1472,400,'_menu_item_target',''),(1473,400,'_menu_item_classes','a:1:{i:0;s:0:\"\";}'),(1474,400,'_menu_item_xfn',''),(1475,400,'_menu_item_url',''),(1477,401,'_menu_item_type','post_type'),(1478,401,'_menu_item_menu_item_parent','425'),(1479,401,'_menu_item_object_id','369'),(1480,401,'_menu_item_object','page'),(1481,401,'_menu_item_target',''),(1482,401,'_menu_item_classes','a:1:{i:0;s:0:\"\";}'),(1483,401,'_menu_item_xfn',''),(1484,401,'_menu_item_url',''),(1846,503,'_edit_lock','